import React, { useState } from 'react'
import 'bootstrap/dist/css/bootstrap.css'

const App = () => {
  const [persons, setPersons] = useState([
    {
      name: 'Arto Hellas',
      number: '045-123456'
    },
    {
      name: 'Jerry Salonen',
      number: '040-222222'
    },
    {
      name: 'Esko Esimerkki',
      number: '888888888'
    }
  ])  
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [nameSearch, setNameSearch] = useState('')
  const [showContacts, setShowContacts] = useState(true)

  const nameListener = (event) => {
    setNewName(event.target.value)
  }

  const numberListener = (event) => {
    setNewNumber(event.target.value)
  }

  const nameSearchListener = (event) => {
    setNameSearch(event.target.value)

    const contactsToShow = showContacts
    ? persons
    : persons.filter(person => person.name.startsWith(nameSearch))
  }

  const addContact = (event) => {
    event.preventDefault()
    let nameExists = false

    persons.forEach((contactTemp) => {
      if (contactTemp.name === newName) {
        nameExists = true
      }
    })

    if (!nameExists) {
      const newContact = {
        name: newName,
        number: newNumber
      }
      setPersons(persons.concat(newContact))
      setNewName('')
      setNewNumber('')
    } else {
      alert(`${newName} on jo puhelinluettelossa!`)
    }
  }

  const mapNames = () => contactsToShow.map(person =>
    <p key={person.name}>{person.name} {person.number}</p>
  )

  return (
    <div className="container">

      <h2 className="display-3">Puhelinluettelo</h2>

      <h2 className="display-4">Rajaa hakua</h2>

      <form className="col-md-8 my-md-3">
        <div className="form-group my-md-2">
          <label htmlFor="nameSearch">Rajaa nimen mukaan:</label>
          <input type="text" id="nameSearch" className="form-control" value={nameSearch} onChange={nameSearchListener} />
        </div>
      </form>

      <h2 className="display-4">Lisää uusi</h2>

      <form className="col-md-8 my-md-3">
        <div className="form-group my-md-2">
          <label htmlFor="nameField">Nimi:</label>
          <input type="text" id="nameField" className="form-control" value={newName} onChange={nameListener} />

          <label htmlFor="numberField">Puhelinnumero:</label>
          <input type="text" id="numberField" className="form-control" value={newNumber} onChange={numberListener} />
        </div>

        <div className="my-md-2">
          <button className="btn btn-primary" type="submit" onClick={addContact}>lisää</button>
        </div>
      </form>

      <h2 className="display-4">Numerot</h2>

      <div className="col-sm my-md-3">
        {mapNames()}
      </div>
    </div>
  )

}

export default App
