import React, { useState } from 'react'
import 'bootstrap/dist/css/bootstrap.css'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ])
  const [newName, setNewName] = useState('')

  const nameListener = (event) => {
    setNewName(event.target.value)
  }

  const addName = (event) => {
    event.preventDefault()
    let nameExists = false

    persons.forEach((contactTemp) => {
      if (contactTemp.name === newName) {
        nameExists = true
      }
    })
    console.log(nameExists)
    if (!nameExists) {
      const newContact = {
        name: newName
      }
      setPersons(persons.concat(newContact))
      setNewName('')
      console.log(persons)
    } else {
      alert(`${newName} on jo puhelinluettelossa!`)
    }
  }

  const mapNames = () => persons.map(person =>
    <p key={person.name}>{person.name}</p>
  )

  return (
    <div className="container">
      <h2 className="display-3">Puhelinluettelo</h2>
      <form className="col-md-8 my-md-3">
        <div className="form-group my-md-2">
          <label htmlFor="nameField">Nimi: </label>
          <input type="text" id="nameField" className="form-control" value={newName} onChange={nameListener} />
        </div>
        <div className="my-md-2">
          <button className="btn btn-primary" type="submit" onClick={addName}>lisää</button>
        </div>
      </form>
      <h2 className="display-4">Numerot</h2>
      <div className="col-sm my-md-3">
        {mapNames()}
      </div>
    </div>
  )

}

export default App
